package servfront

const CONTROLLER_TEMPLATE string = "package controller\n\nimport (\n\t\"microservice/middleware\"\n\n\t\"github.com/denizakturk/servfront/bridge\"\n)\n\ntype {{.ControllerName}} struct {\n\tmiddleware.ControllerMiddleware\n}\n{{$methods := .Method}}\n{{$controllerName := .ControllerName}}\n{{range $method := $methods}}\nfunc (c *{{$controllerName}}) {{$method.Name}}() *bridge.ServiceResponse {\n\tresponse := &bridge.ServiceResponse{}\n\t{{if ne $method.RequestModelName \"\"}}\n\trequest := &model.{{$method.RequestModelName}}\n\t{{end}}\n\t{{if ne $method.ResponseModelName \"\"}}\n\tresponseModel := &model.{{$method.ResponseModelName}}\n\t{{end}}\n\n\tresponse.Data = responseModel\n\treturn response\n}\n{{end}}\n"